{
    "AuthFlow": "USER_PASSWORD_AUTH",
    "ClientId": "{{cognitoClientId}}",
    "AuthParameters": {
        "USERNAME": "{{username}}",
        "PASSWORD": "{{password}}"
    }
}

{
  "name": "EEG Training Session",
  "type": "eeg_training",
  "description": "EEG-based neurofeedback training session.",
  "configuration": {
    "duration": 300,
    "protocol": "alpha_enhancement"
  },
  "estimatedDuration": 300
}

{
  "name": "Pre-Training State Questionnaire",
  "type": "questionnaire",
  "description": "Assesses emotional and cognitive readiness before BCI training.",
  "configuration": {
    "questionnaireId": "PreState"
  },
  "estimatedDuration": 5
}

{
  "id": "PreState",
  "data": {
    "name": "Pre-Training State",
    "description": "Assesses emotional and cognitive readiness before BCI training",
    "estimatedTime": 5,
    "questions": [
      {
        "id": "mood",
        "text": "How would you rate your current mood?",
        "type": "scale",
        "scale": { "min": 1, "max": 7 },
        "required": true
      }
    ]
  }
}

{
  "data": {
    "name": "BCI Training Study",
    "description": "A 21-day protocol combining EEG training with daily, weekly, and trait questionnaires.",
    "sessionTypes": {
      "DAILY": {
        "name": "Daily Session",
        "questionnaires": ["PreState", "PhysicalState", "CurrentState", "EndState", "PQ", "TLX"],
        "tasks": ["TASK#PRE_STATE", "TASK#PHYSICAL_STATE", "TASK#TRAIN_EEG", "TASK#CURRENT_STATE", "TASK#END_STATE", "TASK#PQ", "TASK#TLX"],
        "estimatedDuration": 25,
        "schedule": "daily"
      },
      "WEEKLY": {
        "name": "Weekly Session",
        "questionnaires": ["IPAQ"],
        "tasks": ["TASK#IPAQ"],
        "estimatedDuration": 20,
        "schedule": "weekly"
      },
      "TRAIT": {
        "name": "Trait Questionnaire Session",
        "questionnaires": ["VVIQ", "ATI", "FMI", "MIQ-RS", "Edinburgh", "MentalRotation", "16PF5", "IndexLearningStyles"],
        "tasks": ["TASK#TRAIT_BANK"],
        "estimatedDuration": 115,
        "schedule": "once"
      }
    }
  },
  "questionnaireConfig": {
    "schedule": {
      "PreState": "every_session",
      "PhysicalState": "every_session",
      "CurrentState": "every_session",
      "EndState": "every_session",
      "PQ": "every_session",
      "TLX": "every_session",
      "IPAQ": "weekly",
      "VVIQ": "once",
      "ATI": "once",
      "FMI": "once",
      "MIQ-RS": "once",
      "Edinburgh": "once",
      "MentalRotation": "once",
      "16PF5": "once",
      "IndexLearningStyles": "once"
    }
  }
}

{
  "sessionType": "DAILY",
  "sessionName": "Daily Training Session 1",
  "description": "First daily training session with EEG and questionnaires",
  "date": "2024-01-15"
}

